/* Criar um módulo que calcule a média de uma coleção de números.
Para isso crie um arquivo chamado calcula-media.js.
No módulo exporte uma função media(arr) que recebe como argumento um array de
números. */

var calculaMedia = function(arr){
    var dividendo = arr.length
    var divisor = arr.reduce((divisor, reduzido) => divisor + reduzido)
    return divisor / dividendo
}

var calculaMediaIdadeAluno = function(alunos){
    var dividendo = alunos.length
    var divisor = 0
    //var divisor = alunos.reduce((divisor, reduzido) => divisor + reduzido.idade)
    for(i=0;i<dividendo;i++)
        divisor += alunos[i].idade
    return divisor / dividendo
}
    

exports.media = calculaMedia
exports.calculaMediaIdadeAluno = calculaMediaIdadeAluno


